import 'package:flutter/material.dart';\n\nclass PayScreen extends StatelessWidget {\n  const PayScreen({super.key});\n\n  @override\n  Widget build(BuildContext context) {\n    return Scaffold(\n      appBar: AppBar(\n        title: const Text(\n          'Nexora Pay',\n          style: TextStyle(fontWeight: FontWeight.bold),\n        ),\n        actions: [\n          IconButton(\n            icon: const Icon(Icons.history),\n            onPressed: () {},\n          ),\n          IconButton(\n            icon: const Icon(Icons.notifications_outlined),\n            onPressed: () {},\n          ),\n        ],\n      ),\n      body: SingleChildScrollView(\n        child: Column(\n          crossAxisAlignment: CrossAxisAlignment.start,\n          children: [\n            // Balance Card\n            Container(\n              margin: const EdgeInsets.all(16),\n              padding: const EdgeInsets.all(24),\n              decoration: BoxDecoration(\n                gradient: const LinearGradient(\n                  colors: [Color(0xFF6C63FF), Color(0xFF5A52D5)],\n                  begin: Alignment.topLeft,\n                  end: Alignment.bottomRight,\n                ),\n                borderRadius: BorderRadius.circular(20),\n                boxShadow: [\n                  BoxShadow(\n                    color: const Color(0xFF6C63FF).withOpacity(0.3),\n                    blurRadius: 20,\n                    offset: const Offset(0, 10),\n                  ),\n                ],\n              ),\n              child: Column(\n                crossAxisAlignment: CrossAxisAlignment.start,\n                children: [\n                  Row(\n                    mainAxisAlignment: MainAxisAlignment.spaceBetween,\n                    children: [\n                      const Text(\n                        'Total Balance',\n                        style: TextStyle(\n                          color: Colors.white70,\n                          fontSize: 14,\n                        ),\n                      ),\n                      Container(\n                        padding: const EdgeInsets.symmetric(horizontal: 12, vertical: 4),\n                        decoration: BoxDecoration(\n                          color: Colors.white.withOpacity(0.2),\n                          borderRadius: BorderRadius.circular(12),\n                        ),\n                        child: const Text(\n                          'USD',\n                          style: TextStyle(\n                            color: Colors.white,\n                            fontSize: 12,\n                            fontWeight: FontWeight.bold,\n                          ),\n                        ),\n                      ),\n                    ],\n                  ),\n                  const SizedBox(height: 8),\n                  const Text(\n                    '\$12,547.89',\n                    style: TextStyle(\n                      color: Colors.white,\n                      fontSize: 36,\n                      fontWeight: FontWeight.bold,\n                    ),\n                  ),\n                  const SizedBox(height: 4),\n                  const Text(\n                    '+\$247.89 this month',\n                    style: TextStyle(\n                      color: Color(0xFF00D4AA),\n                      fontSize: 14,\n                    ),\n                  ),\n                ],\n              ),\n            ),\n            // Quick Actions\n            Padding(\n              padding: const EdgeInsets.symmetric(horizontal: 16),\n              child: Row(\n                mainAxisAlignment: MainAxisAlignment.spaceEvenly,\n                children: [\n                  _buildQuickAction(\n                    context,\n                    Icons.send,\n                    'Send',\n                    const Color(0xFF6C63FF),\n                  ),\n                  _buildQuickAction(\n                    context,\n                    Icons.call_received,\n                    'Receive',\n                    const Color(0xFF00D4AA),\n                  ),\n                  _buildQuickAction(\n                    context,\n                    Icons.account_balance,\n                    'Withdraw',\n                    const Color(0xFFFF6B6B),\n                  ),\n                  _buildQuickAction(\n                    context,\n                    Icons.swap_horiz,\n                    'Exchange',\n                    const Color(0xFFFFB800),\n                  ),\n                ],\n              ),\n            ),\n            const SizedBox(height: 24),\n            // Payment Methods\n            Padding(\n              padding: const EdgeInsets.symmetric(horizontal: 16),\n              child: Row(\n                mainAxisAlignment: MainAxisAlignment.spaceBetween,\n                children: [\n                  const Text(\n                    'Payment Methods',\n                    style: TextStyle(\n                      fontSize: 18,\n                      fontWeight: FontWeight.bold,\n                    ),\n                  ),\n                  TextButton(\n                    onPressed: () {},\n                    child: const Text('Add New'),\n                  ),\n                ],\n              ),\n            ),\n            const SizedBox(height: 8),\n            _buildPaymentMethod(\n              'M-Pesa',\n              'Mobile Money',\n              Icons.phone_android,\n              const Color(0xFF00D852),\n            ),\n            _buildPaymentMethod(\n              'PayPal',\n              'Online Payment',\n              Icons.paypal,\n              const Color(0xFF0070BA),\n            ),\n            _buildPaymentMethod(\n              'Visa Card',\n              '•••• 4532',\n              Icons.credit_card,\n              const Color(0xFF1A1F71),\n            ),\n            _buildPaymentMethod(\n              'Crypto Wallet',\n              'Bitcoin, Ethereum',\n              Icons.currency_bitcoin,\n              const Color(0xFFFF9500),\n            ),\n            const SizedBox(height: 24),\n            // Recent Transactions\n            const Padding(\n              padding: EdgeInsets.symmetric(horizontal: 16),\n              child: Text(\n                'Recent Transactions',\n                style: TextStyle(\n                  fontSize: 18,\n                  fontWeight: FontWeight.bold,\n                ),\n              ),\n            ),\n            const SizedBox(height: 8),\n            ListView.builder(\n              shrinkWrap: true,\n              physics: const NeverScrollableScrollPhysics(),\n              itemCount: 5,\n              itemBuilder: (context, index) {\n                return _buildTransaction(\n                  'Transaction ${index + 1}',\n                  index % 2 == 0 ? 'Received' : 'Sent',\n                  index % 2 == 0 ? '+\$${(index + 1) * 50}.00' : '-\$${(index + 1) * 30}.00',\n                  index % 2 == 0,\n                );\n              },\n            ),\n            const SizedBox(height: 80),\n          ],\n        ),\n      ),\n    );\n  }\n\n  Widget _buildQuickAction(BuildContext context, IconData icon, String label, Color color) {\n    return Column(\n      children: [\n        Container(\n          width: 60,\n          height: 60,\n          decoration: BoxDecoration(\n            color: color.withOpacity(0.1),\n            borderRadius: BorderRadius.circular(15),\n          ),\n          child: Icon(icon, color: color, size: 28),\n        ),\n        const SizedBox(height: 8),\n        Text(\n          label,\n          style: const TextStyle(\n            fontSize: 12,\n            fontWeight: FontWeight.w600,\n          ),\n        ),\n      ],\n    );\n  }\n\n  Widget _buildPaymentMethod(String title, String subtitle, IconData icon, Color color) {\n    return Container(\n      margin: const EdgeInsets.symmetric(horizontal: 16, vertical: 6),\n      padding: const EdgeInsets.all(16),\n      decoration: BoxDecoration(\n        color: Colors.grey[100],\n        borderRadius: BorderRadius.circular(12),\n      ),\n      child: Row(\n        children: [\n          Container(\n            padding: const EdgeInsets.all(12),\n            decoration: BoxDecoration(\n              color: color.withOpacity(0.1),\n              borderRadius: BorderRadius.circular(10),\n            ),\n            child: Icon(icon, color: color, size: 24),\n          ),\n          const SizedBox(width: 16),\n          Expanded(\n            child: Column(\n              crossAxisAlignment: CrossAxisAlignment.start,\n              children: [\n                Text(\n                  title,\n                  style: const TextStyle(\n                    fontWeight: FontWeight.bold,\n                    fontSize: 16,\n                  ),\n                ),\n                const SizedBox(height: 2),\n                Text(\n                  subtitle,\n                  style: TextStyle(\n                    color: Colors.grey[600],\n                    fontSize: 13,\n                  ),\n                ),\n              ],\n            ),\n          ),\n          const Icon(Icons.chevron_right, color: Colors.grey),\n        ],\n      ),\n    );\n  }\n\n  Widget _buildTransaction(String title, String type, String amount, bool isReceived) {\n    return ListTile(\n      leading: Container(\n        padding: const EdgeInsets.all(10),\n        decoration: BoxDecoration(\n          color: isReceived\n              ? const Color(0xFF00D4AA).withOpacity(0.1)\n              : const Color(0xFFFF6B6B).withOpacity(0.1),\n          borderRadius: BorderRadius.circular(10),\n        ),\n        child: Icon(\n          isReceived ? Icons.arrow_downward : Icons.arrow_upward,\n          color: isReceived ? const Color(0xFF00D4AA) : const Color(0xFFFF6B6B),\n          size: 20,\n        ),\n      ),\n      title: Text(\n        title,\n        style: const TextStyle(fontWeight: FontWeight.w600),\n      ),\n      subtitle: Text(\n        type,\n        style: TextStyle(color: Colors.grey[600], fontSize: 12),\n      ),\n      trailing: Text(\n        amount,\n        style: TextStyle(\n          fontWeight: FontWeight.bold,\n          fontSize: 16,\n          color: isReceived ? const Color(0xFF00D4AA) : const Color(0xFFFF6B6B),\n        ),\n      ),\n    );\n  }\n}\n